  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/webpack-vite/vite/vite-study/在vite中接入现代化的CSS过程化方案/node_modules/.pnpm/windicss@3.5.6/node_modules/windicss/cli/node_modules:/mnt/d/webpack-vite/vite/vite-study/在vite中接入现代化的CSS过程化方案/node_modules/.pnpm/windicss@3.5.6/node_modules/windicss/node_modules:/mnt/d/webpack-vite/vite/vite-study/在vite中接入现代化的CSS过程化方案/node_modules/.pnpm/windicss@3.5.6/node_modules:/mnt/d/webpack-vite/vite/vite-study/在vite中接入现代化的CSS过程化方案/node_modules/.pnpm/node_modules"
